mapper.select:
   SELECT

	FROM  
	WHERE a.id = #{id}

SELECT a.code as id,a.name,d.name as maintainName,a.maintain_person as maintainPerson,c.company_name AS property,a.is_ture as isTure FROM dsj_new_house_directory_auth a LEFT JOIN dsj_agent d ON a.maintain_person=d.user_id LEFT JOIN dsj_company c ON d.company = c.id
AND c.company_name like concat('%',#{property},'%') AND d.name like concat('%',#{maintainName},'%') AND a.edit=1
ORDER BY a.id DESC LIMIT #{pageFirst} , #{pageSize}

SELECT a.id,a.name,a.is_ture as isTure,d.name as eveloperName FROM dsj_new_house_directory_auth a LEFT JOIN dsj_evelopers d ON a.evelopers_person=d.user_id
AND a.id like concat('%',#{id},'%') AND a.name like concat('%',#{name},'%') AND (a.is_ture=#{is_ture} OR a.is_ture=#{not_ture}) AND d.name like concat('%',#{eveloperName},'%') AND a.edit=1
ORDER BY a.id DESC LIMIT #{pageFirst} , #{pageSize}

