mapper.select:
		SELECT
	     COUNT(1) 
               FROM
	     dsj_new_house_directory_auth a
	     LEFT JOIN dsj_im_directory i ON i.house_id=a.id

		  AND a.is_ture = #{isTure}
		  AND i.agent_id = #{agentId}
		  AND a.status = #{status}
		  AND a.name like concat('%',#{name},'%')

SELECT a.id, a. NAME, a. STATUS, a.around_min_price AS aroundMinPrice, a.image AS pictureUrl, d.id AS spellName, l.id AS oldName, ht.housetypeNames FROM dsj_new_house_directory_auth a LEFT JOIN dsj_new_house_picture_auth p ON p.object_id = a.id LEFT JOIN dsj_agent_directory d ON d.newHouse_id = a.id LEFT JOIN dsj_loupan_show l ON l.house_id = a.id LEFT JOIN ( SELECT GROUP_CONCAT(result ORDER BY roomNum) AS housetypeNames, dicId FROM ( SELECT CONCAT(a1.room, "(", count, ")") AS result, a1.dicId, a1.roomNum FROM ( SELECT CONCAT( CASE room WHEN 1 THEN '' WHEN 2 THEN '' WHEN 3 THEN '' WHEN 4 THEN '' WHEN 5 THEN '' WHEN 6 THEN '' WHEN 7 THEN '' WHEN 8 THEN '' WHEN 9 THEN '' END, '' ) AS room, COUNT(1) AS count, dic_id AS dicId, room AS roomNum FROM dsj_new_house_type_auth GROUP BY dic_id, room ) a1 ) b GROUP BY dicId ) ht ON ht.dicId = a.id
AND a.data_type = #{dataType}
AND (a.name like CONCAT("%",#{name},"%") or a.id = #{name})
GROUP BY a.id ORDER BY a.id DESC LIMIT #{pageFirst} , #{pageSize}

SELECT a.id, a. NAME, a. STATUS, a.code, a.around_min_price AS aroundMinPrice, i.agent_id AS maintainPerson, a.image AS pictureUrl, i.is_duty AS isDuty, d.id AS spellName, l.id AS oldName, ht.housetypeNames, a.auth_status AS authStatus, a.is_ture AS isTure FROM dsj_new_house_directory_auth a LEFT JOIN dsj_im_directory i ON i.house_id = a.id LEFT JOIN dsj_new_house_picture_auth p ON p.object_id = a.id LEFT JOIN dsj_agent_directory d ON d.newHouse_id = a.id LEFT JOIN dsj_loupan_show l ON l.house_id = a.id LEFT JOIN ( SELECT GROUP_CONCAT(result ORDER BY roomNum) AS housetypeNames, dicId FROM ( SELECT CONCAT(a1.room, "(", count, ")") AS result, a1.dicId, a1.roomNum FROM ( SELECT CONCAT( CASE room WHEN 1 THEN '' WHEN 2 THEN '' WHEN 3 THEN '' WHEN 4 THEN '' WHEN 5 THEN '' WHEN 6 THEN '' WHEN 7 THEN '' WHEN 8 THEN '' WHEN 9 THEN '' END, '' ) AS room, COUNT(1) AS count, dic_id AS dicId, room AS roomNum FROM dsj_new_house_type_auth GROUP BY dic_id, room ) a1 ) b GROUP BY dicId ) ht ON ht.dicId = a.id
AND a.is_ture = #{isTure} AND i.agent_id = #{agentId} AND a.status = #{status} AND i.is_duty = 1
AND (a.name like CONCAT("%",#{name},"%") or a.id = #{name})
AND a.auth_status = #{authStatus}
GROUP BY a.id ORDER BY a.id DESC LIMIT #{pageFirst} , #{pageSize}

