mapper.select:
SELECT a.id, a.NAME, a.STATUS, a.phone, a.area_leval_three_name AS areaName3, a.trading_area_name AS tradingAreaName, a.around_min_price AS aroundMinPrice, a.dic_trait_name AS dicTraitName, a.image AS pictureUrl, ht.housetypeNames, dn.title AS 'newsTitle', dm.mobile, a.address FROM dsj_new_house_directory_auth a LEFT JOIN dsj_agent_directory d ON d.newHouse_id = a.id LEFT JOIN dsj_new_house_news dn ON dn.house_id=a.id LEFT JOIN dsj_mobile dm ON dm.house_id = a.id LEFT JOIN ( SELECT GROUP_CONCAT(result ORDER BY roomNum) AS housetypeNames, dicId FROM ( SELECT CONCAT(a1.room, "(", count, ")") AS result, a1.dicId, a1.roomNum FROM ( SELECT CONCAT( CASE room WHEN 1 THEN '' WHEN 2 THEN '' WHEN 3 THEN '' WHEN 4 THEN '' WHEN 5 THEN '' WHEN 6 THEN '' WHEN 7 THEN '' WHEN 8 THEN '' WHEN 9 THEN '' END, '' ) AS room, COUNT(1) AS count, dic_id AS dicId, room AS roomNum FROM dsj_new_house_type_auth GROUP BY dic_id, room ) a1 ) b GROUP BY dicId ) ht ON ht.dicId = a.id
AND d.agent_id = #{userId} AND a.is_ture = #{isTure} AND a.data_type = #{dataType}
GROUP BY a.id ORDER BY a.around_min_price asc, d.create_time DESC, a.around_min_price DESC, d.create_time ASC, a.id DESC LIMIT #{pageFirst} , #{pageSize}

select count(new.counts) from (SELECT COUNT(1) counts FROM dsj_new_house_directory_auth a LEFT JOIN dsj_agent_directory d ON d.newHouse_id = a.id LEFT JOIN dsj_new_house_news dn ON dn.house_id=a.id LEFT JOIN dsj_mobile dm ON dm.house_id = a.id LEFT JOIN ( SELECT GROUP_CONCAT(result ORDER BY roomNum) AS housetypeNames, dicId FROM ( SELECT CONCAT(a1.room, "(", count, ")") AS result, a1.dicId, a1.roomNum FROM ( SELECT CONCAT( CASE room WHEN 1 THEN '' WHEN 2 THEN '' WHEN 3 THEN '' WHEN 4 THEN '' WHEN 5 THEN '' WHEN 6 THEN '' WHEN 7 THEN '' WHEN 8 THEN '' WHEN 9 THEN '' END, '' ) AS room, COUNT(1) AS count, dic_id AS dicId, room AS roomNum FROM dsj_new_house_type_auth GROUP BY dic_id, room ) a1 ) b GROUP BY dicId ) ht ON ht.dicId = a.id
AND d.agent_id = #{userId} AND a.is_ture = #{isTure} AND a.data_type = #{dataType}
GROUP BY a.id) new

SELECT a.id, a.NAME, a.STATUS, a.phone, a.area_leval_three_name AS areaName3, a.trading_area_name AS tradingAreaName, a.around_min_price AS aroundMinPrice, a.dic_trait_name AS dicTraitName, a.image AS pictureUrl, ht.housetypeNames, dn.title AS 'newsTitle', dm.mobile, a.address FROM dsj_pc_new_house_page page LEFT JOIN dsj_new_house_directory_auth a on a.id=page.newHouse_id LEFT JOIN dsj_new_house_news dn ON dn.house_id=a.id LEFT JOIN dsj_mobile dm ON dm.house_id = a.id LEFT JOIN ( SELECT GROUP_CONCAT(result ORDER BY roomNum) AS housetypeNames, dicId FROM ( SELECT CONCAT(a1.room, "(", count, ")") AS result, a1.dicId, a1.roomNum FROM ( SELECT CONCAT( CASE room WHEN 1 THEN '' WHEN 2 THEN '' WHEN 3 THEN '' WHEN 4 THEN '' WHEN 5 THEN '' WHEN 6 THEN '' WHEN 7 THEN '' WHEN 8 THEN '' WHEN 9 THEN '' END, '' ) AS room, COUNT(1) AS count, dic_id AS dicId, room AS roomNum FROM dsj_new_house_type_auth GROUP BY dic_id, room ) a1 ) b GROUP BY dicId ) ht ON ht.dicId = a.id
AND a.is_ture = #{isTure} AND a.data_type = #{dataType}
GROUP BY a.id ORDER BY a.around_min_price asc, a.auth_time DESC, a.around_min_price DESC, a.auth_time ASC, a.id DESC LIMIT #{pageFirst} , #{pageSize}

select count(new.counts) from (SELECT COUNT(1) counts FROM dsj_pc_new_house_page page LEFT JOIN dsj_new_house_directory_auth a on a.id=page.newHouse_id LEFT JOIN dsj_new_house_news dn ON dn.house_id=a.id LEFT JOIN dsj_mobile dm ON dm.house_id = a.id LEFT JOIN ( SELECT GROUP_CONCAT(result ORDER BY roomNum) AS housetypeNames, dicId FROM ( SELECT CONCAT(a1.room, "(", count, ")") AS result, a1.dicId, a1.roomNum FROM ( SELECT CONCAT( CASE room WHEN 1 THEN '' WHEN 2 THEN '' WHEN 3 THEN '' WHEN 4 THEN '' WHEN 5 THEN '' WHEN 6 THEN '' WHEN 7 THEN '' WHEN 8 THEN '' WHEN 9 THEN '' END, '' ) AS room, COUNT(1) AS count, dic_id AS dicId, room AS roomNum FROM dsj_new_house_type_auth GROUP BY dic_id, room ) a1 ) b GROUP BY dicId ) ht ON ht.dicId = a.id
AND a.is_ture = #{isTure} AND a.data_type = #{dataType}
GROUP BY a.id) new
